new Vue({
  name: "Users",
  vuetify: new Vuetify(),
  data: function data() {
    return {
      dialog: false,
      dialogDelete: false,
      categoryName: "Polyurethane Rods",
      headers: [{
        text: "Part Number",
        align: "start",
        sortable: true,
        value: "partNumber"
      }, {
        text: "Date Added",
        sortable: true,
        value: "dateAdded"
      }, {
        text: "Actions",
        value: "actions",
        sortable: false
      }],
      parts: [],
      editedIndex: -1,
      editedItem: {
        partNumber: "",
        dateAdded: ""
      },
      defaultItem: {
        partNumber: "",
        dateAdded: ""
      }
    };
  },
  computed: {
    formTitle: function formTitle() {
      return this.editedIndex === -1 ? "New Item" : "Edit Item";
    }
  },
  watch: {
    dialog: function dialog(val) {
      val || this.close();
    },
    dialogDelete: function dialogDelete(val) {
      val || this.closeDelete();
    }
  },
  created: function created() {
    this.initialize();
  },
  methods: {
    initialize: function initialize() {
      this.parts = [{
        linkID: 6450,
        partNumber: "SPROD1.5-70",
        dateAdded: "13\/10\/2021 09:54:22"
      }, {
        linkID: 6473,
        partNumber: "SPROD3-95",
        dateAdded: "13\/10\/2021 09:57:01"
      }, {
        linkID: 6474,
        partNumber: "SPROD4-70",
        dateAdded: "13\/10\/2021 09:57:23"
      }, {
        linkID: 6475,
        partNumber: "SPROD4-80",
        dateAdded: "13\/10\/2021 09:57:26"
      }, {
        linkID: 6476,
        partNumber: "SPROD4-90",
        dateAdded: "13\/10\/2021 09:57:29"
      }];
    },
    editItem: function editItem(item) {
      this.editedIndex = this.parts.indexOf(item);
      this.editedItem = Object.assign({}, item);
      this.dialog = true;
    },
    deleteItem: function deleteItem(item) {
      this.editedIndex = this.parts.indexOf(item);
      this.editedItem = Object.assign({}, item);
      this.dialogDelete = true;
    },
    deleteItemConfirm: function deleteItemConfirm() {
      // define what item to delete
      part = this.editedItem;
      linkID = part.linkID;
      partNumber = part.partNumber;
      alert("Deleting Item Now..." + linkID + " which is " + partNumber);
      this.parts.splice(this.editedIndex, 1);
      this.closeDelete();
    },
    close: function close() {
      var _this = this;

      this.dialog = false;
      this.$nextTick(function () {
        _this.editedItem = Object.assign({}, _this.defaultItem);
        _this.editedIndex = -1;
      });
    },
    closeDelete: function closeDelete() {
      var _this2 = this;

      this.dialogDelete = false;
      this.$nextTick(function () {
        _this2.editedItem = Object.assign({}, _this2.defaultItem);
        _this2.editedIndex = -1;
      });
    },
    save: function save() {
      if (this.editedIndex > -1) {
        Object.assign(this.parts[this.editedIndex], this.editedItem); // define what item to edit

        part = this.editedItem;
        linkID = part.linkID;
        partNumber = part.partNumber;
        alert("Editing Item Now..." + linkID + " which is " + partNumber);
      } else {
        // define what the new part is
        part = this.editedItem;
        partNumber = part.partNumber;
        alert("Saving New Part " + partNumber);
        this.parts.push(this.editedItem);
      }

      this.close();
    }
  }
});
